#Fonction pour obtenir le résultat
def calculate_score(image_data):
    result = score(image_data)
    # Convertir tout les chiffres de négatif à positif
    result = [abs(score) for score in result]
    return result

result = calculate_score(image_data)

if result:
    predicted_digit = result.index(max(result))
    print("Predicted digit:", predicted_digit)
else:
    print("No valid scores available.")

print(f"Score: {result}")

